    // === Parse ===
    @Test
    public void should_move_up_one_floor() throws Exception {
        assertEquals(1, ElevatorParser.parse(0, "("));
    }

    @Test
    public void should_move_down_one_floor() throws Exception {
        assertEquals(-1, ElevatorParser.parse(0, ")"));
    }

    @Test
    public void should_stay_on_same_floor() throws Exception {
        assertEquals(0, ElevatorParser.parse(0, ")("));
    }

    @Test
    public void should_handle_different_base() throws Exception {
        assertEquals(49, ElevatorParser.parse(50, ")"));
    }

