    // === Notify Basement ===

    @Test
    public void should_go_in_basement_immediately_if_already_in_basement() {
        assertEquals(0, ElevatorParser.notifyBasement(-1, "("));
    }

    @Test
    public void should_go_in_basement_at_position_1() {
        assertEquals(1, ElevatorParser.notifyBasement(0, ")"));
    }

    @Test
    public void should_go_in_basement_at_position_5() {
        assertEquals(5, ElevatorParser.notifyBasement(0, "()())"));
    }

